/**
 *
 * @param {string} key
 * @param {(result:string) => void} callback
 */
function getData(key, callback) {
  chrome.storage.local.get([key], callback);
}

/**
 *
 * @param {string} key
 * @param {string} value
 */
function setData(key, value) {
  let g = {};
  g[key] = value;
  chrome.storage.local.set(g, function () {
    console.log("Storage[" + key + "] set to " + value);
  });
}

/**
 *
 * @param {string} msg
 */
